// constants
import Web3 from "web3";
import GoodNFT from "/Users/intvirtue/Last-final/src/contracts/GoodNFT.json";
// log
import { fetchData } from "../data/dataActions";

const connectRequest = () => {
  return {
    type: "CONNECTION_REQUEST",
  };
};

const connectSuccess = (payload) => {
  return {
    type: "CONNECTION_SUCCESS",
    payload: payload,
  };
};

const connectFailed = (payload) => {
  return {
    type: "CONNECTION_FAILED",
    payload: payload,
  };
};

const updateAccountRequest = (payload) => {
  return {
    type: "UPDATE_ACCOUNT",
    payload: payload,
  };
};

export const connect = () => {
  return async (dispatch) => {
    dispatch(connectRequest());
    if (window.ethereum) {
      let web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
      try {
        const accounts = await window.ethereum.request({
          method: "eth_accounts",

        });
        const networkId = await window.ethereum.request({
          method: "net_version",
        });
        const NetworkData = await GoodNFT.networks[networkId];
        if (NetworkData) {
          const GoodNFTObj = new web3.eth.Contract(
            GoodNFT.abi,
            NetworkData.address
          );
          dispatch(
            connectSuccess({
              account: accounts[0],
              smartContract: GoodNFTObj,
              web3: web3,
            })
          );
          // Add listeners start
          window.ethereum.on("accountsChanged", (accounts) => {
            dispatch(updateAccount(accounts[0]));

          });
          window.ethereum.on("chainChanged", () => {
            window.location.reload();
          });
          // Add listeners end
        } else {
          dispatch(connectFailed("Change network to Ropsten Testnet."));
        }
      } catch (err) {
        dispatch(connectFailed("Something went wrong."));
      }
    } else {
      dispatch(connectFailed("Install Metamask."));
    }
  };
};

export const updateAccount = (account) => {
  return async (dispatch) => {
    dispatch(updateAccountRequest({ account: account }));
    dispatch(fetchData(account));
  };
};
